import { memo } from "react";

const style = {
  width: "100%",
  height: "200px",
  backgroundColor: "khaki",
};
export const ChildArea = memo((props) => {
  const { open } = props;
  console.log("ChildAreaがレンダリングされました！");
  return (
    <>
      {open ? (
        <div style={style}>
          <p>子コンポーネント</p>
        </div>
      ) : null}
    </>
  );
});
